document.querySelectorAll("input[type='text']").forEach(input => {
    let label = input.previousElementSibling;
    input.addEventListener("focus", () => {
        label.style.top = "-25px";
    })
    
   input.addEventListener("focusout", () => {
    if(input.value === ""){
        label.style.top = "0px";
    }else{
        label.style.top = "-12px";
    }
   })
});
